  const [movie, setMovie] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const requests =await axios.get(request.fetchNetflixOriginals) 
      setMovie(
        request.data.results[
          Math.floor(Math.random() * request.data.results.length - 1)
         ]
      );
        return request;
    }
     fetchData();
  }, []); 

